import React, { Component } from 'react';
import {portfolioData} from '../../data/portfolioData';
import Project from './Project';

export default class ProjectList extends Component {
    state = {
        portfolio : portfolioData,
        radios: 
        [
            {id: 1, value: 'javascript'},
            {id: 2, value: 'css'},
            {id: 3, value: 'react'},
            {id: 4, value: 'java'}
        ] ,
        selectedRadio : 'javascript'
    };
    handelRadio =(event) => {
        let radio = event.target.value;
        this.setState({selectedRadio : radio})
    }
    render() {
        let projects = this.state.portfolio; 
        let radios = this.state.radios; 
        let selectedRadio = this.state.selectedRadio; 
        return (
            <div className="portfolioContent">
                <ul className="radioDisplay">
                    {
                        radios.map((radio) => {
                            return(
                                <li key={radio.id}>
                                    <input type="radio"
                                    name="radio"
                                    checked={radio.value === selectedRadio}
                                    value = {radio.value}
                                    id={radio.value}
                                    onChange={this.handelRadio}
                                    />
                                    <label htmlFor={radio.value}>
                                        {radio.value}
                                    </label>
                                </li>
                            )
                        })
                    }
                </ul>
                <div className="projects">
                    {projects.filter(item => item.languages.includes(selectedRadio))
                    
                    .map(item => {
                            return(
                                <Project
                                key = {item.id}
                                item = {item}
                                />
                            )
                        })
                    }
                </div>  
            </div>
        );
    }
}
